    public function activateLandingPage($memberno){     
     	
    
    	$filter_memberno = mysqli_real_escape_string($this->db, $memberno);
    	
    	//				0			1			2			3			4
    	$q="SELECT  `MemberNo`, `FullName`, `PageName`, `TelMobile`, `Whatsapp` ";
    	$q=$q."FROM `tbl_landing_page` ";
		$q=$q."WHERE `MemberNo`='".$filter_memberno."' ";
		
		$r=mysqli_query($this->db,$q) or die(mysqli_error($this->db));
		
		if($rows=mysqli_fetch_row($r)){
			$obj->status = 0;
			$obj->msg = "Already activated";	
			return json_encode($obj);
		} else { 
	    	
	    	//SELECT `MemberNo`, `MemberUsername`, `MDUsername`, `FullName`,  `TelMobile`, `StatusReg` FROM `tbl_member_info` WHERE `StatusReg`='2'
	    	//				0			1			2			3			4		5			6
	    	$q="SELECT  `MemberNo`, `MemberUsername`, `MDUsername`, `FullName`,  `TelMobile`, `StatusReg` ";    	
	    	$q=$q."FROM  `tbl_member_info` ";
			$q=$q."WHERE  `MemberNo`='".$filter_memberno."' ";
			
			$r=mysqli_query($this->db,$q) or die(mysqli_error($this->db));
			
			if($rows=mysqli_fetch_row($r)){
				
				$MemberNo = $rows[0];
				$MemberUsername = $rows[1];
				$MDUsername = $rows[2];
				$FullName = $rows[3];
				$TelMobile = $rows[4];
				$StatusReg  = $rows[5];
				
				//remove special char
				$tmp = preg_replace("/[^a-zA-Z]/", "", $MemberUsername);

				$PageName = $tmp;
				
				if( $StatusReg < 2 ){
				
					$obj->status = 0;
					$obj->msg = "Status Not SDE";	
					return json_encode($obj);
					
				} else {
				
					$Photo = "data:image/png;base64,";
					//$Photo = preg_replace("", "", $Photo);

					$Background = "data:image/jpeg;base64,";
					//$Background = preg_replace("", "", $Background);
				
					$q = "Insert Into `tbl_landing_page` (`MemberNo`, `FullName`, `PageName`, `TelMobile`, `Photo`, `Background`, `DateCreated`, `DateLastUpdated`) ";
					$q = $q."Values ( '".mysqli_real_escape_string($this->db, $MemberNo)."', ";
					$q = $q."'".mysqli_real_escape_string($this->db, $FullName)."', ";
					$q = $q."'".mysqli_real_escape_string($this->db, $PageName)."', ";
					$q = $q."'".mysqli_real_escape_string($this->db, $TelMobile)."', ";
					$q = $q."'".mysqli_real_escape_string($this->db, $Photo)."', ";
					$q = $q."'".mysqli_real_escape_string($this->db, $Background)."', ";
					$q = $q."'".mysqli_real_escape_string($this->db, $this->dt->dateTodayTime())."', ";
					$q = $q."'".mysqli_real_escape_string($this->db, $this->dt->dateTodayTime())."' )";
					
					$res=mysqli_query($this->db,$q);
					
					if( $res ){
						$obj->status = 1;
						$obj->msg = "Your Landingpage is activated.";	
						return json_encode($obj);
					}
					
				}//end if( $StatusReg < 2 ){
				
				
			} else {
				$obj->status = 0;
				$obj->msg = "Not member";	
				return json_encode($obj);

			}//end if($rows=mysqli_fetch_row($r)){
		
		}//end if($rows=mysqli_fetch_row($r)){
    
    }
    
    public function validatePageName($json_str){
    
    	$usr = json_decode($json_str,true);
    
    	$filter_memberno = mysqli_real_escape_string($this->db, $usr['memberno']);
    	$filter_pagename = mysqli_real_escape_string($this->db, $usr['pagename'] );
    	
    	//				0			1			2			3			4
    	$q="SELECT  `MemberNo`, `FullName`, `PageName`, `TelMobile`, `Whatsapp` ";
    	$q=$q."FROM `tbl_landing_page` ";
		$q=$q."WHERE `MemberNo`<>'".$filter_memberno."' AND `PageName` LIKE '".$filter_pagename."' ";
		
		$r=mysqli_query($this->db,$q) or die(mysqli_error($this->db));
		
		if($rows=mysqli_fetch_row($r)){
			$obj->status = 0;
			$obj->msg = "<font color='red'>This PageName has been used '".$usr['pagename']."' </font>";	
			$obj->pagename = $usr['pagename'];
			return json_encode($obj);
		}
		
		
		//`MemberUsername`
		//				0			1			2			3			4
    	$q="SELECT  `MemberNo`, `MemberUsername`, `MDUsername` ";
    	$q=$q."FROM `tbl_member_info` ";
		$q=$q."WHERE (`MemberNo`<>'".$filter_memberno."' AND `MemberUsername` LIKE '".$filter_pagename."') ";
		
		$r=mysqli_query($this->db,$q) or die(mysqli_error($this->db));
		
		if($rows=mysqli_fetch_row($r)){
			$obj->status = 0;
			$obj->msg = "<font color='red'>This PageName already has been used '".$usr['pagename']."' </font>";	
			$obj->pagename = $usr['pagename'];
			return json_encode($obj);
		}
		
		
		//`MemberUsername`, `MDUsername`
		//				0			1			2			3			4
    	$q="SELECT  `MemberNo`, `MemberUsername`, `MDUsername` ";
    	$q=$q."FROM `tbl_member_info` ";
		$q=$q."WHERE (`MemberNo`<>'".$filter_memberno."' AND `MDUsername` LIKE '".$filter_pagename."') ";
		
		$r=mysqli_query($this->db,$q) or die(mysqli_error($this->db));
		
		if($rows=mysqli_fetch_row($r)){
			$obj->status = 0;
			$obj->msg = "<font color='red'>This PageName already has been used '".$usr['pagename']."' </font>";	
			$obj->pagename = $usr['pagename'];
			return json_encode($obj);
		}

		
		
		if( true ){
			//remove special char
			$pagename = preg_replace("/[^a-zA-Z0-9]/", "", $usr['pagename']);
			
			$obj->status = 1;
			$obj->msg = "";
			$obj->pagename = $pagename;	
			return json_encode($obj);

		}		
		
    }
    
    
